using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.Threading.Tasks;

namespace BunnyHeadquarters
{
    class CAPTCHA
    {
        //pax pumpkin
        //static string data = "";
        //paxlaurien
        readonly static string data = "";


        static void Main(string[] args)
        {
            Int64 summation = 0;
            int thisDigit;
            int nextDigit;
            int lookAhead = data.Length / 2;
            int peekPoint;
            // rotational (halfway match/add)
            for (int position = 0; position < data.Length; position++)
            {
                thisDigit = Int32.Parse(data[position].ToString());
                peekPoint = (position + lookAhead > (data.Length - 1)) ? position - lookAhead : position + lookAhead;
                nextDigit = Int32.Parse(data[peekPoint].ToString());
                summation += (thisDigit == nextDigit) ? thisDigit : 0;

            }
            // rotational (next digit match/add)
            //foreach (char x in data)
            //{
            //    thisDigit = Int32.Parse(x.ToString());
            //    if (lastDigit == -1) firstDigit = thisDigit;
            //    summation += ((lastDigit == thisDigit) ? thisDigit : 0);
            //    lastDigit = thisDigit;
            //}
            //summation += (lastDigit == firstDigit) ? lastDigit : 0;
            Console.WriteLine("final: " + summation);
        }
    }
}
